import React, { useState, useEffect } from 'react';
import { AuthenticatedTemplate, UnauthenticatedTemplate} from '@azure/msal-react';
import { useIsAuthenticated } from "@azure/msal-react";
import { SignInButton } from "./components/SignInButton";
import { SignOutButton } from "./components/SignOutButton";
import { Navbar}   from 'react-bootstrap';
import MenuG from './MenuG';

export default function App() {
  const [textoNick, setTextoNick] = useState("");
  const [dataLoaded, setDataLoaded] = useState(false); // Variable de estado para controlar si los datos del servicio web se han cargado
  const isAuthenticated = useIsAuthenticated();

  const fetchData = async () => {
    try {
      const response = await fetch('http://wservicesdes.brazilsouth.cloudapp.azure.com/rest/WsRetActiveDirectoryAzure', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          Clave: 'kfjshf84rwkjfsdklgfw49@254325jhsdgft',
          EntradaWs3: {
            BuscarPorDominioYusuario: {
              NickName: '',
              Dominio: ''
            },
            BuscarPorMail: {
              Mail: 'gpalmieri@socovesa.cl'
            },
            BuscarPorNombre: {
              Nombre: ''
            }
          }
        })
      });

      console.log('*** Mensaje 1')

      const responseData = await response.json();
      if (responseData.ColeccionDatosActiveDirectory && responseData.ColeccionDatosActiveDirectory.Cuentas) {
        console.log('*** Mensaje 2 '+ responseData.ColeccionDatosActiveDirectory.Cuentas[0].CuentaDeUsuario.NickName)
        setNickName(responseData.ColeccionDatosActiveDirectory.Cuentas[0].CuentaDeUsuario.NickName)
      }
      setDataLoaded(true); // Marcar que los datos del servicio web se han cargado exitosamente
    } catch (error) {
      console.error('Error al obtener datos del servicio web:', error);
      setDataLoaded(true); // Marcar que los datos del servicio web se han cargado, aunque haya ocurrido un error
    }
  };

  useEffect(() => {
    if (isAuthenticated) {
      fetchData();
    }
  }, [isAuthenticated]);

  return (
    <div className="Prueba" >
      <Navbar bg="dark" variant="dark" className="navbarStyle">
        <Navbar.Brand href="#home">
          &nbsp;&nbsp;Autenticaci√≥n
        </Navbar.Brand>
        <div className="collapse navbar-collapse justify-content-end">
          {isAuthenticated ? <SignOutButton /> : <SignInButton />}
        </div>
      </Navbar>
      <div className="App">
        <AuthenticatedTemplate>
          <div style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom:0 }}>
            {dataLoaded ? ( // Renderizar el componente MenuG solo si los datos del servicio web se han cargado
              <MenuG Probando={nickName} /> // Pasar el valor de nickname como prop al componente MenuG
            ) : (
            <p>Cargando datos...</p> // Mostrar mensaje de carga mientras se obtienen los datos del servicio web
            )}
          </div>
        </AuthenticatedTemplate>
        <UnauthenticatedTemplate>
          <br></br>
          <br></br>
          <h5>
            <center> Por favor, ingrese sus credenciales para acceder al Sistema</center>
          </h5>
        </UnauthenticatedTemplate>
      </div>
    </div>
  );
}
