import React, { useState } from 'react';
import { AuthenticatedTemplate, UnauthenticatedTemplate, useMsal } from '@azure/msal-react';
import Button from 'react-bootstrap/Button';
import { PageLayout } from './components/PageLayout';
import { loginRequest } from './authConfig';
import { callMsGraph } from './graph';
import { ProfileData } from './components/ProfileData';
import MenuG from './MenuG';

import './App.css';

export default function App() {
  const { instance, accounts } = useMsal();
  const [graphData, setGraphData] = useState(null);
  const [showMenuG, setShowMenuG] = useState(false);

  const handleMenuGClick = () => {
    setShowMenuG(true);
    setGraphData(null); // Agregar esta línea para eliminar los datos del perfil una vez que se haga clic en el botón "Show MenuG1"
  }

  if (showMenuG) {
    return <MenuG />;
  }

  return (
    <PageLayout>
      <center>
        <div className="App">
          <AuthenticatedTemplate>
            {/* Eliminar la renderización condicional de ProfileContent y ProfileData */}
            <Button variant="secondary" onClick={handleMenuGClick}>
              Show MenuG1
            </Button>
          </AuthenticatedTemplate>

          <UnauthenticatedTemplate>
            <h5>
              <center></center>
            </h5>
          </UnauthenticatedTemplate>
        </div>
      </center>
    </PageLayout>
  );
}
