import React, { useState, useEffect } from 'react';
import { AuthenticatedTemplate, UnauthenticatedTemplate, useMsal } from '@azure/msal-react';
import Button from 'react-bootstrap/Button';
import { PageLayout } from './components/PageLayout';
import { loginRequest } from './authConfig';
import { callMsGraph } from './graph';
import { ProfileData } from './components/ProfileData';
import MenuG from './MenuG';

import './App.css';

const ProfileContent = () => {
  const { instance, accounts } = useMsal();
  const [graphData, setGraphData] = useState(null);
  const [showMenuG, setShowMenuG] = useState(false);

  const handleProfileDataRequest = () => {
    instance
      .acquireTokenSilent({
        ...loginRequest,
        account: accounts[0],
      })
      .then((response) => {
        callMsGraph(response.accessToken).then((response) => setGraphData(response));
       
      });
  }

  const handleMenuGClick = () => {
    setShowMenuG(true);
    
  }

  useEffect(() => {
    if (accounts && accounts.length > 0) {
      setShowMenuG(true);
    }
  }, [accounts]);



  return (
    <div style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0 }}>
      {/* Renderizar el componente MenuG en la misma pantalla ocupando toda la pantalla */}
      <MenuG />
    </div>
  );
};

export default function App() {


  return (
    <PageLayout>
        <div className="App">
          <AuthenticatedTemplate>
            <ProfileContent />
          </AuthenticatedTemplate>

          <UnauthenticatedTemplate>
            <h5>
              <center>Por favor, ingrese sus</center>
            </h5>
          </UnauthenticatedTemplate>
        </div>

    </PageLayout>
  );
}
